INSERT INTO `eventos` (`id`, `titulo`, `dia_del_evento`, `imagen`, `descripcion`, `publicador`) VALUES
(114, '¡Evento especial en Hobba!', '14 Enero, 2020', '../imagenes/event-img/b2d9700f0f.png', '<p style=\"text-align: justify;\">Llega el <strong>Guaypaunt </strong>siendo una competencia donde los concursantes deben evadir obst&aacute;culos para acumular la mayor cantidad de puntos. El primer puesto se lleva m&aacute;s que un Ultra Rare, placa totalmente &uacute;nica, diamantes e ingredientes del crafteo. Ser&aacute; abierto de <strong>lunes a jueves</strong> a las <strong>21:00 HPE</strong>.</p>\r\n\r\n<p style=\"text-align: justify;\">-<strong> LUFantasie</strong>.&nbsp;</p>\r\n', 'LUFantasie'),
(115, '¡Diversión con los Moderadores!', '14 Enero, 2020', '../imagenes/event-img/fd9c277112.png', '<p style=\"text-align: justify;\">Un mes especial, y qu&eacute; mejor forma de celebrarlo con la <strong>Hora Feliz</strong> patrocinada por los <strong>moderadores</strong>. Distintos juegos, desaf&iacute;os y retos por cumplir. Los horarios est&aacute;n sujetos en la p&aacute;gina principal del hotel.</p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.</p>\r\n', 'LUFantasie'),
(116, 'Servidor de Hobba y pura diversión', '14 Enero, 2020', '../imagenes/event-img/8584d8647b.png', '<p style=\"text-align: justify;\">&iexcl;&Uacute;nete a la diversi&oacute;n que nos proporciona el canal del servidor <strong>Discord de Hobba Hotel</strong>! En este mismo te podr&aacute;s enterar de las &uacute;ltimas novedades, pr&oacute;ximos juegos, eventos, autom&aacute;ticos y mucho m&aacute;s. &iquest;C&oacute;mo ingreso? Espera a que un Hotel Manager mande una alerta o lateral para invitarle al servidor.</p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.</p>\r\n', 'LUFantasie'),
(118, 'Diversión asegurada con LUFantasie', '14 Enero, 2020', '../imagenes/event-img/1a21b59035.png', '<p style=\"text-align: justify;\">Diariamente seguimos innovando para que la diversi&oacute;n no se acabe en nuestra web. &iexcl;De ahora en m&aacute;s tenemos <strong>autom&aacute;ticos</strong>! Con el agradecimiento de varios usuarios que decidieron colaborar, podr&aacute;s jugar a la hora que sea, sumar puntos y llevarte premios. Semanalmente se reiniciar&aacute;n los puntos (s&aacute;bado) y el usuario que tenga m&aacute;s puntos se llevar&aacute; un contundente premio.</p>\r\n\r\n<p style=\"text-align: justify;\">Para ir a los autom&aacute;ticos solo basta que busques las salas del usuario: <strong>LUFautomaticos</strong></p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.&nbsp;<br />\r\n&nbsp;</p>\r\n', 'LUFantasie'),
(119, '¡Viernes de pura diversión!', '17 Enero, 2020', '../imagenes/event-img/d42eff7d25.png', '<p>Seguimos innovando para que no te pierdas de las &uacute;ltimas novedades que traemos junto a LUFantasie, de ahora en m&aacute;s cada viernes lo titularemos como: &iexcl;<strong>Viernes de Reacciones</strong>!, presentando as&iacute; distintos: eventos, juegos y concursos v&iacute;a facebook: LUFantasie. &iquest;Est&aacute;s listo para ganar?&nbsp;</p>\r\n\r\n<p>Facebook de LUFantasie: <a href=\"https://www.facebook.com/LUFantasieWeb/?epa=SEARCH_BOX\">clic aqu&iacute;.</a>&nbsp;</p>\r\n\r\n<p>- <strong>LUFantasie</strong>.&nbsp;</p>\r\n', 'LUFantasie'),
(120, 'Los talentos de Hobba', '18 Enero, 2020', '../imagenes/event-img/3969b366a0.png', '<p style=\"text-align: justify;\">Llega el <strong>Hobba&#39;s Got Talent</strong>, donde puedes presentar una cantidad de habilidades como: canto, imitaci&oacute;n de cantante, utilizaci&oacute;n de instrumento, construcci&oacute;n express, dise&ntilde;os de moda, poes&iacute;a, improvisaci&oacute;n, mon&oacute;logo, rol, entre muchas cosas m&aacute;s. &iquest;Est&aacute;s listo para participar? &nbsp;Este evento se realiza cada s&aacute;bado en el horario <strong>21:00 HPE</strong>.</p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.</p>\r\n', 'LUFantasie'),
(121, 'Charla en el Parque Infobus', '25 Enero, 2020', '../imagenes/event-img/343f632675.png', '<p style=\"text-align: justify;\">Este s&aacute;bado 25 de enero te esperamos en el m&iacute;tico Parque Infobus para conmemorar una nueva charla de la mano del Hotel Manager AndresMaxima con motivo: <strong>contaminaci&oacute;n en las aguas marinas</strong>. &iquest;Est&aacute;s listo para asistir? M&aacute;s informaci&oacute;n en nuestra secci&oacute;n de noticias.</p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.</p>\r\n', 'LUFantasie'),
(122, 'Viernes de diversión', '24 Enero, 2020', '../imagenes/event-img/2522ca6e3d.png', '<p style=\"text-align: justify;\">Cada viernes conmemoramos un nuevo <strong>viernes de reacciones</strong>. &iexcl;Los emojis se apoderan de LUFantasie y es tu oportunidad de participar! Presentamos distintos eventos, desaf&iacute;os y retos para que puedas ganar unos cuantos premios. &iquest;Todav&iacute;a no participaste?</p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.&nbsp;</p>\r\n', 'LUFantasie'),
(123, 'Presentamos una nueva edición', '20 Enero, 2020', '../imagenes/event-img/eac9cff340.png', '<p style=\"text-align: justify;\">&iexcl;<strong>Tu Secreto en LUFantasie</strong>! Cada lunes y mi&eacute;rcoles presentamos esta nueva edici&oacute;n donde podr&aacute;s contar todas tus an&eacute;cdotas, secretos, chistes o cualquier cosa que se te venga en la mente (siempre y cuando respetando la Manera Hobba). &iquest;C&oacute;mo postulo mi secreto? Por medio de nuestro formulario: <em><a href=\"https://docs.google.com/forms/d/e/1FAIpQLSeqpaXsxA5XE8EJ8clXc0PnI3oDDssIXz2aDugL5lJHqAMoHw/viewform\">clic aqu&iacute;</a></em>.</p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.</p>\r\n', 'LUFantasie'),
(124, '¡El evento más esperado!', '20 Enero, 2020', '../imagenes/event-img/31534eed30.png', '<p style=\"text-align: justify;\">Este evento llevado a cabo por LUFantasie revela y queda en evidencia cada &quot;fail&quot; o error cometido por nuestros usuarios. &iquest;LUFantasie es el mejor usuario de todos los tiempos? FUCKING MINT. No te pierdas cada edici&oacute;n que se lleva adelante los lunes y viernes, &iexcl;Solo en LUFantasie!</p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.&nbsp;</p>\r\n', 'LUFantasie'),
(125, 'La gala más esperada', '20 Enero, 2020', '../imagenes/event-img/55d2a2dcbd.png', '<p style=\"text-align: justify;\">&iexcl;Se aproximan los <strong>Hobba Awards</strong>! Hasta entonces queda habilitado el formulario para presentadores de la gala m&aacute;s esperada por todos los usuarios de nuestra comunidad. Una noche de festejo y celebraci&oacute;n. Es tu oportunidad de mostrar tus atuendos extravagantes, pasear por la alfombra roja, presenciar la ceremonia y celebrarlo en la fiesta awards. &iquest;Est&aacute;s listo? M&aacute;s informaci&oacute;n: <em><a href=\"https://www.hobba.tv/awards\">clic aqu&iacute;</a></em>.</p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.</p>\r\n', 'LUFantasie'),
(126, '¡Semana de pura diversión!', '20 Enero, 2020', '../imagenes/event-img/0bc4cdbae0.png', '<p style=\"text-align: justify;\">Llega el <strong>Room Of The Week</strong> y <strong>Game Of The Week</strong> siendo concursos semanales con una tem&aacute;tica asignada con la funci&oacute;n de sumergir a los usuarios de Hobba en un mismo entorno, ya sea jugando o conviviendo con el resto de Hobbas. M&aacute;s informaci&oacute;n: <em><a href=\"https://www.hobba.tv/competitions\">clic aqu&iacute;</a></em>.</p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.&nbsp;</p>\r\n', 'LUFantasie'),
(127, '¡La gala más esperada del año!', '27 Enero, 2020', '../imagenes/event-img/919d27ed2b.png', '<p style=\"text-align: justify;\">No te pierdas la gala m&aacute;s esperada de los <strong>Hobba Awards </strong>junto a LUFantasie. Mostraremos como se prepara el equipo y todos los preparativos que se est&aacute;n llevando adelante. &iexcl;Imperdible! Disponible en nuestra p&aacute;gina <u><a href=\"https://www.facebook.com/LUFantasieWeb\">facebook: LUFantasie</a></u>.</p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.</p>\r\n', 'LUFantasie'),
(128, 'Medio pato, medio corazón', '27 Enero, 2020', '../imagenes/event-img/9b902594eb.png', '<p style=\"text-align: justify;\">Evento especial llevado a cabo en nuestras redes sociales, &iquest;Este mes rom&aacute;ntico conseguir&aacute;s pareja? Desc&uacute;brelo en <strong>Tu Medio Pato</strong> desde la organizaci&oacute;n LUFantasie preparamos los mejores eventos para que lo disfrutes al m&aacute;ximo. Se llevar&aacute; a cabo los lunes, mi&eacute;rcoles y viernes solo en LUFantasie.</p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.</p>\r\n', 'LUFantasie'),
(129, '¿Qué tan Hobba usuario eres?', '27 Enero, 2020', '../imagenes/event-img/0af8eaf51a.png', '<p style=\"text-align: justify;\"><strong>Test Fantasie </strong>llega este mes de febrero para divertirte con preguntas totalmente &uacute;nicas, alocadas y para dejarnos sin habla por mucho tiempo. &iquest;Qu&eacute; tan ilegal eres?, desc&uacute;brelo pr&oacute;ximamente en nuestros eventos v&iacute;a <a href=\"https://www.facebook.com/LUFantasieWeb\"><u>facebook: LUFantasie</u></a>. Se llevar&aacute; a cabo los s&aacute;bados solo en LUFantasie.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.</p>\r\n', 'LUFantasie'),
(130, 'Uniendo dos Hobba corazones', '27 Enero, 2020', '../imagenes/event-img/525c7b2511.png', '<p style=\"text-align: justify;\">Un mes rom&aacute;ntico y lo celebramos junto a LUFantasie y todas las fieles parejas. &iquest;Tienes alg&uacute;n enamorad@? Este evento podr&iacute;a ser para ti porque nuestra meta es que encuentres a tu medio coraz&oacute;n y quien sabe... LUFantasie sea el padrino de la boda. &iquest;Est&aacute;s listo para participar? Se llevar&aacute; a cabo los domingos solo en LUFantasie.</p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.&nbsp;</p>\r\n', 'LUFantasie'),
(131, 'Los preparativos están en marcha', '27 Enero, 2020', '../imagenes/event-img/56b168ae36.png', '<p style=\"text-align: justify;\">LUFantasie te informar&aacute; con datos exclusivos de c&oacute;mo se van preparando los Hobba Awards, &iquest;listo para conocer las primicias? Pr&oacute;ximamente disponible en nuestras redes sociales.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.</p>\r\n', 'LUFantasie'),
(132, 'TIPs Gala Awards', '27 Enero, 2020', '../imagenes/event-img/9903476fad.png', '<p style=\"text-align: justify;\">&iquest;No sabes qu&eacute; usar para los Hobba Awards? &iquest;Est&aacute;s nervioso y temes caerte en la alfombra roja? Conoce los <strong>TIPs profesionales</strong> que llegan de la mano de nuestro equipo administrativo. Sin duda traemos la exclusiva de todos los consejos para estar en forma y caminar correctamente. &iexcl;Tac&oacute;n, punta!&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">- <strong>LUFantasie</strong>.&nbsp;</p>\r\n', 'LUFantasie'),
(133, 'Quiniela Awards 2019', '1 Febrero, 2020', '../imagenes/event-img/01cbf0b21c.png', '<p style=\"text-align: justify;\">En los <strong>Awards </strong>hay diferentes secciones en las que solo una persona tendr&aacute; el honor de ganar el premio. &iquest;Podr&iacute;as predecir qui&eacute;nes ser&aacute;n los ganadores de este a&ntilde;o? &iexcl;Participa en la <strong>quiniela </strong>que tenemos en nuestro&nbsp;<a href=\"https://www.facebook.com/LUFantasieWeb/posts/1482502688585589?__xts__[0]=68.ARCqKQz4CaOZ3ZuOy41G-qEnyaqaLTg-NIaqtZPkiumaSx8e76c8Vn0ozEIib7pwFA9pvAUCFc9p_V2_toamyKbZC8lk_bsA1EkSip0D5AwVkkQ7ATJg_Rt7Kz2WZnhExMRP3iG9Ts0wzJ3Ap1o0vAPUXTjdKBmCcEymULGAiyv74m6vATPBmONidWNl0SvNyZJrjBvQBkWTRBPUAjgN4FNn1bcb1eYxkoj7aamsCvgA6jzhh2vQKs8ur44v8JQ_fCfZoy3HwV5aPHoyiaseLKQY338ssiyu_en9bHX1-brN8CaCoImeOIboJTcaLZa0QhwC-Vcf3npO2OIUiute71PAR9XRD1fgAslDqCrQCYYWaKnu6duLMd1--6e_pSw3oo2eXozO6lsFB9dNyrghE5sv00NGasy68eqBshYdvGf2lOGd1176ua7U1m4uH5xHz8dTcmq56ikxYOd9AXOu&amp;__tn__=-R\"><strong>Facebook</strong></a>! Tendr&aacute;s la oportunidad de ganar un incre&iacute;ble premio si aciertas en m&iacute;nimo 5 categor&iacute;as. &iexcl;Te esperamos!</p>\r\n', 'LUFantasie'),
(134, '¿Ya enviaste tus votos?', '8 Febrero, 2020', '../imagenes/event-img/add84320ad.png', '<p style=\"text-align: justify;\">La <strong>encuesta </strong>para las votaciones de los <strong>Hobba Awards 2019</strong> ya ha sido publicada. Si a&uacute;n no has mandado tu voto, da <strong><a href=\"https://bit.ly/2GGnRAo\">click</a><a href=\"https://bit.ly/2GGnRAo\">&nbsp;</a>aqu&iacute;</strong> para hacerlo directamente. &iexcl;Tu voto es importante!</p>\r\n', 'LUFantasie'),
(135, 'Llegó la hora de acumular puntos', '1 Febrero, 2020', '../imagenes/event-img/744ada0c38.png', '<p style=\"text-align: justify;\">En febrero se viene una variedad inmensa de eventos tanto en el hotel como en nuestra web, pero hay uno en espec&iacute;fico que toma gran poder en los usuarios: la <strong>Gymkhana</strong>. &iexcl;No te pierdas de ning&uacute;n d&iacute;a! Acumula puntos y s&eacute; el ganador de este mes.</p>\r\n', 'LUFantasie'),
(136, 'Tu medio... ¿pato?', '1 Febrero, 2020', '../imagenes/event-img/978c913ea4.png', '<p style=\"text-align: justify;\">Las redes de LUFantasie toman el poder de entretener en este mes. &iexcl;Es momento de descubrir qui&eacute;n es tu medio pato! Atento a nuestro <a href=\"https://www.facebook.com/LUFantasieWeb/?__tn__=kC-R&amp;eid=ARCHFPrPvqhzOPCiVGY3or-saO9UvUvbfZ20WgNbcJV6xQpVHY0ipqH4MPGWyaTzp-X9y_yJjmCIGhF0&amp;hc_ref=ARRlzfKjNY1awWV3Npn1EguI5btVl4ke_8SeUfj_KcqAGQYuEhHuIPQ2m3b5c-ViBzc&amp;fref=nf&amp;__xts__[0]=68.ARDR_JFQ3Wlq6LKd_jv2KScRT6T8Hlt6uK1OPJLB_OGFxCmhN_qFPar3lDuTJuGDUm26SeWkB_zmya4XUEWjeySATTpuxMaWO_DXAEM6C9piOoUPJPGfck5_RhpuszJDZUDEk_86s9he2SftQjbHnmZHkutoi6QeKOVOEWaSaVJXA4eYRU0gl92FtLsd7LaYLbn_gEv4FM9F-FNKwC177rQduAIhk5Pw9FBTtNdJG2w59QKk-gUuJzdn5M35BBN2M_PZeB8ysHzlFlDiJa9m5iJqLSd1yfK7BD9_QrH5nE3AWFPaX1bwNuLepno_QhhljhLuC2wO8c7eKiw-rs_0q6p6Yg\"><u><strong>Facebook</strong></u></a><strong> </strong>para participar en este evento. &iquest;Qui&eacute;n ser&aacute; tu otra mitad? &iexcl;Desc&uacute;brelo!</p>\r\n', 'LUFantasie'),
(137, '¿Te han roto el corazón?', '1 Febrero, 2020', '../imagenes/event-img/046c490947.png', '<p style=\"text-align: justify;\">Si alguna vez te han roto el coraz&oacute;n, este evento es para ti. &iquest;Qu&eacute; tal si nos cuentas alguna an&eacute;cdota sobre tus desamores? Tienes m&aacute;s informaci&oacute;n en nuestra p&aacute;gina de <a href=\"https://www.facebook.com/LUFantasieWeb/?__tn__=kC-R&amp;eid=ARCHFPrPvqhzOPCiVGY3or-saO9UvUvbfZ20WgNbcJV6xQpVHY0ipqH4MPGWyaTzp-X9y_yJjmCIGhF0&amp;hc_ref=ARRlzfKjNY1awWV3Npn1EguI5btVl4ke_8SeUfj_KcqAGQYuEhHuIPQ2m3b5c-ViBzc&amp;fref=nf&amp;__xts__[0]=68.ARDR_JFQ3Wlq6LKd_jv2KScRT6T8Hlt6uK1OPJLB_OGFxCmhN_qFPar3lDuTJuGDUm26SeWkB_zmya4XUEWjeySATTpuxMaWO_DXAEM6C9piOoUPJPGfck5_RhpuszJDZUDEk_86s9he2SftQjbHnmZHkutoi6QeKOVOEWaSaVJXA4eYRU0gl92FtLsd7LaYLbn_gEv4FM9F-FNKwC177rQduAIhk5Pw9FBTtNdJG2w59QKk-gUuJzdn5M35BBN2M_PZeB8ysHzlFlDiJa9m5iJqLSd1yfK7BD9_QrH5nE3AWFPaX1bwNuLepno_QhhljhLuC2wO8c7eKiw-rs_0q6p6Yg\"><u><strong>Facebook</strong></u></a>. &iexcl;Participa y gana premios! Hay que sacar el lado bueno de cada historia.</p>\r\n', 'LUFantasie'),
(138, '¿Qué tan... eres?', '1 Febrero, 2020', '../imagenes/event-img/221269a9a6.png', '<p style=\"text-align: justify;\">Llegaron los tests a LUFantasie mediante el <strong>Test Fantasie</strong> en nuestro <a href=\"https://www.facebook.com/LUFantasieWeb/?__tn__=kC-R&amp;eid=ARCHFPrPvqhzOPCiVGY3or-saO9UvUvbfZ20WgNbcJV6xQpVHY0ipqH4MPGWyaTzp-X9y_yJjmCIGhF0&amp;hc_ref=ARRlzfKjNY1awWV3Npn1EguI5btVl4ke_8SeUfj_KcqAGQYuEhHuIPQ2m3b5c-ViBzc&amp;fref=nf&amp;__xts__[0]=68.ARDR_JFQ3Wlq6LKd_jv2KScRT6T8Hlt6uK1OPJLB_OGFxCmhN_qFPar3lDuTJuGDUm26SeWkB_zmya4XUEWjeySATTpuxMaWO_DXAEM6C9piOoUPJPGfck5_RhpuszJDZUDEk_86s9he2SftQjbHnmZHkutoi6QeKOVOEWaSaVJXA4eYRU0gl92FtLsd7LaYLbn_gEv4FM9F-FNKwC177rQduAIhk5Pw9FBTtNdJG2w59QKk-gUuJzdn5M35BBN2M_PZeB8ysHzlFlDiJa9m5iJqLSd1yfK7BD9_QrH5nE3AWFPaX1bwNuLepno_QhhljhLuC2wO8c7eKiw-rs_0q6p6Yg\"><u><strong>Facebook</strong></u></a>. &iquest;Quieres saber qu&eacute; tan ilegal eres? &iexcl;Responde el test! No olvides que cada s&aacute;bado tendr&aacute;s un test nuevo para contestar.</p>\r\n', 'LUFantasie'),
(139, '¿Ya encontraste tu medio pato?', '10 Febrero, 2020', '../imagenes/event-img/74a9ec923b.png', '<p style=\"text-align: justify;\">El d&iacute;a de San Valent&iacute;n est&aacute; a la vuelta de la esquina, y es por ello que en LUFantasie te traemos un evento en el que podr&aacute;s encontrar a tu otra mitad. &iquest;Ya lo conoces? &iexcl;Visita nuestro <strong>Facebook </strong>y participa en <strong>Tu Medio Pato</strong>! Todos los <strong>lunes</strong>, <strong>mi&eacute;rcoles </strong>y <strong>viernes </strong>deber&aacute;s estar atento.</p>\r\n', 'LUFantasie'),
(140, 'Me acaba de llegar un paquete de... ¡parejas!', '10 Febrero, 2020', '../imagenes/event-img/4fd211643f.png', '<p style=\"text-align: justify;\">Lleg&oacute; el momento de deslumbrar a la comunidad con un grandioso <strong>look </strong>en pareja. &iquest;Tienes alguno preparado? Puede ser con tu mejor amigo o con tu hobba-novio. Si no lo tienes, &iexcl;comienza a crearlo! Lo necesitar&aacute;s para el evento en <strong>Facebook </strong>que traemos para ti los <strong>martes </strong>y <strong>jueves</strong>.</p>\r\n', 'LUFantasie'),
(141, '¿Qué tan viciado estás a Hobba?', '10 Febrero, 2020', '../imagenes/event-img/d32f8a723e.png', '<p style=\"text-align: justify;\">El <strong>Test Fantasie</strong> sigue en pie durante este mes todos los s&aacute;bados, y esta vez podr&aacute;s saber qu&eacute; tan viciado est&aacute;s a Hobba si contestas el Test a tiempo. &iexcl;Prep&aacute;rate para las preguntas! Estate atento a nuestra p&aacute;gina de <strong>Facebook</strong>: <strong>LUFantasie</strong>.</p>\r\n', 'LUFantasie'),
(142, '¡Visita nuestras historias en Facebook!', '10 Febrero, 2020', '../imagenes/event-img/cd234bf634.png', '<p style=\"text-align: justify;\">LUFantasie se preocupa por que tengas algo divertido qu&eacute; hacer todos los d&iacute;as. &iquest;Qu&eacute; tal echarle un vistazo a las famosas historias en <strong>Facebook</strong>? Podr&aacute;s encontrar diferentes din&aacute;micas y anuncios que no te debes de perder. &iexcl;Cada historia disponible por 24 horas!</p>\r\n', 'LUFantasie'),
(143, 'Diario de un corazón roto', '10 Febrero, 2020', '../imagenes/event-img/33d7bdc63f.png', '<p style=\"text-align: justify;\">&iquest;Y cre&iacute;as que los domingos estar&iacute;an vac&iacute;os de diversi&oacute;n? &iexcl;Est&aacute;s equivocado! El <strong>diario de un coraz&oacute;n roto </strong>est&aacute; disponible para que nos cuentes alguna an&eacute;cdota en el amor... &iquest;A ti tambi&eacute;n te han dejado en la <strong>friendzone</strong>? &iexcl;No te lo pierdas! S&aacute;cale provecho a tus historias de desamor.</p>\r\n', 'LUFantasie'),
(144, '¡Prepárate para los juegos!', '10 Febrero, 2020', '../imagenes/event-img/1cae943c1e.png', '<p style=\"text-align: justify;\">Dentro del hotel tambi&eacute;n te ofrecemos uno que otro juego para que te lo pases genial y puedas ganar diferentes premios. Recuerda que podr&aacute;s ganar <strong>puntos </strong>en cada juego (y eventos especiales en nuestro <strong>Facebook</strong>) para un gran premio al final de mes. &iquest;Ya viste el <strong>TOP Puntos Web</strong>?</p>\r\n', 'LUFantasie'),
(145, '¡Viernes de reacciones!', '18 Febrero, 2020', '../imagenes/event-img/b85111448e.png', '<p style=\"text-align: justify;\">&iquest;Te hab&iacute;as olvidado del <strong>Viernes de reacciones</strong>? &iexcl;Espero que no! Pero te recordamos que todos los viernes tenemos eventos para ti en los que... &iexcl;Solo tienes que reaccionar! Y claro, comentar tu nombre de usuario lo m&aacute;s pronto posible. Dale <strong>Me Gusta</strong> a nuestra p&aacute;gina en FB: <a href=\"https://www.facebook.com/LUFantasieWeb/\"><u><strong>LUFantasie</strong></u></a></p>\r\n', 'LUFantasie'),
(146, '¿Capaz de hacer el mejor meme?', '18 Febrero, 2020', '../imagenes/event-img/3a93f5b6a6.png', '<p style=\"text-align: justify;\">Un nuevo concurso lleg&oacute; para ti; un concurso en el que deber&aacute;s <strong>crear el mejor meme</strong> acerca de Hobba. &iquest;Te sientes capaz de hacerlo? Aunque... &iexcl;Puedes mandar m&aacute;s de un meme! Es momento de que pongas en marcha la misi&oacute;n de crear el mejor meme y mandarlo al correo: <u><strong>concursoshobba@gmail.com</strong></u></p>\r\n', 'LUFantasie'),
(147, '¡Hora de construir!', '18 Febrero, 2020', '../imagenes/event-img/e0dac5c3ee.png', '<p style=\"text-align: justify;\">En Hobba tenemos un par de eventos que llevan a&ntilde;os de ser creados. &iexcl;S&eacute; parte de la tradici&oacute;n del <strong>GOTW </strong>(Game of the week) y <strong>ROTW </strong>(Room of the week)! Construye tu sala adecuada a la tem&aacute;tica de la semana y gana puntos para un incre&iacute;ble premio.&nbsp;</p>\r\n', 'LUFantasie'),
(148, '¡Envíale una carta a alguien!', '18 Febrero, 2020', '../imagenes/event-img/c6810b751e.png', '<p style=\"text-align: justify;\">En LUFantasie abrimos un <strong>Buz&oacute;n del Amor</strong> para aquellos enamorados que deseen escribir una carta a sus hobba-seres queridos. &iquest;Ya escribiste la tuya? Si no, puedes dar <a href=\"https://forms.gle/YpK8UoQFD59KEpwy9\"><strong><u>click aqu&iacute;</u></strong></a> para llenar el formulario con los datos correspondientes, y en los siguientes d&iacute;as se publicar&aacute;n las cartas en nuestro FB: <a href=\"https://www.facebook.com/LUFantasieWeb/\"><u><strong>LUFantasie</strong></u></a>. Psst... &iexcl;Puede ser an&oacute;nimo!</p>\r\n', 'LUFantasie'),
(149, 'La llegada del carnaval', '18 Febrero, 2020', '../imagenes/event-img/05da8fda7f.png', '<p style=\"text-align: justify;\">El carnaval lleg&oacute; a Hobba y necesitaremos un buen look para disfrutarlo como se debe. Prep&aacute;rate para este gran evento y... &iexcl;No te lo pierdas! Eventos as&iacute; solo se viven una vez al a&ntilde;o. &iexcl;Y no queremos esperar otro largo a&ntilde;o!</p>\r\n', 'LUFantasie'),
(150, '¿Te interesaría ser parte de nosotros?', '18 Febrero, 2020', '../imagenes/event-img/0b307d93b4.png', '<p style=\"text-align: justify;\">Espero que tu click haya sido por estar interesado en formar parte de nuestro equipo. Actualmente estamos en busca de <strong>Reporteros </strong>que nos mantengan al d&iacute;a con todas las novedades que ocurran en el hotel. &iquest;Nueva ropa, furnis, rares? &iquest;Ascensos, dimisiones? &iexcl;Los Reporteros se encargan de redactar cada noticia! Ponte en contacto con <strong>Stevan </strong>si est&aacute;s interesado.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Y... &iquest;sobre editar im&aacute;genes? &iquest;Crear eventos en nuestro FB? &iquest;Te interesa? Contacta con <strong>issua </strong>para saber m&aacute;s acerca del equipo de los <strong>Redes Sociales</strong>.</p>\r\n', 'LUFantasie'),
(151, '¡Se busca nueva Central de Ayuda!', '2 Marzo, 2020', '../imagenes/event-img/8d4fbd964c.png', '<p style=\"text-align: justify;\">En cada temporada, Hobba cambia de <strong>Central de Ayuda Oficial</strong> para estar acorde la estaci&oacute;n del a&ntilde;o del hemisferio norte de nuestro planeta. Y, si no estabas enterado a&uacute;n, este mes de marzo comienza la <strong>primavera</strong>. &iexcl;As&iacute; que comienza a plantar tulipanes y girasoles! Los necesitar&aacute;s para construir la Central de Ayuda que la Administraci&oacute;n nos pide mediante un <strong>concurso</strong>.</p>\r\n\r\n<p style=\"text-align: justify;\">Lee todo lo que necesitas saber dando <a href=\"https://www.hobba.tv/news/buscamos-central-de-ayuda-primaveral\"><u><strong>click aqu&iacute;</strong></u></a>.</p>\r\n', 'LUFantasie'),
(152, 'Llena tu sala o tu juego durante una semana', '2 Marzo, 2020', '../imagenes/event-img/7dba08b89b.png', '<p style=\"text-align: justify;\">El famoso <strong>GOTW &amp; ROTW</strong> sigue en pie y esta vez con la <strong>tem&aacute;tica jur&aacute;sica</strong>, aquella en la que los dinosaurios masivos y enormes plantas se apoderan del hobba-mundo. Ve preparando tus energ&iacute;as para ganar este concurso sin que te coman los dinosaurios. &iquest;Quieres saber m&aacute;s? Da <a href=\"https://www.hobba.tv/news/marzo-%231-rotw-y-gotw\"><strong>click aqu&iacute;</strong></a> y lee todos los detalles.</p>\r\n', 'LUFantasie'),
(153, '¿Listo para crear los mejores looks?', '2 Marzo, 2020', '../imagenes/event-img/50a4230f32.png', '<p style=\"text-align: justify;\">Al hotel han llegado <strong>m&aacute;s prendas</strong> a tu armario para que est&eacute;s al d&iacute;a con la hobba-moda. &iquest;Ya te las probaste? &iexcl;Y tambi&eacute;n aparecer&aacute; <strong>ropa LTD</strong>! As&iacute; que no te pierdas de ning&uacute;n evento, que seguramente te podr&aacute;s ganar alguna que otra prenda.</p>\r\n', 'LUFantasie'),
(154, '¡RAWR! Los dinosaurios llegaron', '2 Marzo, 2020', '../imagenes/event-img/4eed02b26f.png', '<p style=\"text-align: justify;\">A partir de ahora deber&aacute;s tener mucho m&aacute;s cuidado con los pasillos de Hobba ya que nos hemos teletransportado a la era mesozoica, por lo que los <strong>dinosaurios </strong>podr&iacute;an comernos en cualquier momento. Aunque... &iexcl;No todo es malo! Pues en <strong>LUFantasie </strong>te traemos todo el <strong>calendario de eventos</strong> para que puedas prepararte con anticipaci&oacute;n para cada uno de ellos. Da <a href=\"http://lufantasie.info/calendario\"><strong>click aqu&iacute;</strong></a> y conoce todos los eventos programados.</p>\r\n', 'LUFantasie'),
(155, 'Tap, tap... ¡Trabajando en las oficinas de LUF!', '2 Marzo, 2020', '../imagenes/event-img/59556f2896.png', '<p style=\"text-align: justify;\">&iquest;Ya conociste nuestras oficinas? Aquel lugar en el que nuestro equipo se encarga de mantenerte informado,&nbsp;de entretenerte, y de traerte las actualizaciones al minuto. &Eacute;chale un vistazo a nuestro &aacute;lbum en Facebook y cu&eacute;ntanos qu&eacute; te parece: <a href=\"https://www.facebook.com/pg/LUFantasieWeb/photos/?tab=album&amp;album_id=1503669926468865&amp;__xts__%5B0%5D=68.ARBLqCIzr1O40KyJ4GzClJwvjiRCh0Cxs7hpQJUnJULp-Tk3TaWuox6seLQIFghirLGp9pQKbgBFZEuAaAO4aiSw1_QKRrWDiD9y_CGTv0oAvwCXBl-_PnHjoW15lUydtt6h-3Zo2_gwrVCwQDz5AxL4qWfmOlZcIzYXaD1DzaoBIrVVnSCX-LmDHmpHVXLct8zT-Ya5MUndPd-jxurOo-C0JuF9tMSIUeQW7U-LOR4dHUDHczJo4us8yL19QeOfbK7YLX3yoewkISm4MgwcrQiT34F4A-OiVfHvFE-ixgggVkRgwOOeiAzA9_KTxsdYXVn6OIGlqEsmtcOOTjgEaEwG-FIkAW6nmX6O7mFeQTY7RLcMhka7Ibw2YpEnKsf-AZ2rncqImW0jJbVt-14w8g3rrVKvl0cOvx3TIkP0wVNxXBzmxeeozu3l6N4xkqB3S0yIQcDOtk6BCBs1oTgY&amp;__tn__=-UC-R\"><strong>Oficinas de LUFantasie</strong></a>.</p>\r\n', 'LUFantasie'),
(157, 'Hora de craftear fósiles', '4 Marzo, 2020', '../imagenes/event-img/9d78991bd9.png', '<p style=\"text-align: justify;\">En nuestro <strong>Facebook </strong>tenemos toda la informaci&oacute;n acerca del crafteo de marzo. &iexcl;Empieza a recolectar los f&oacute;siles! <a href=\"https://www.facebook.com/LUFantasieWeb/photos/a.280304765472060/1510749552427569/?type=3&amp;theater\"><u><strong>Click aqu&iacute;</strong></u></a> y revisa la receta.</p>\r\n', 'LUFantasie'),
(158, '¿Sabías que...?', '17 Marzo, 2020', '../imagenes/event-img/e4d2715fe2.png', '<p style=\"text-align: justify;\">&iquest;Sab&iacute;as que en nuestro Facebook contamos con datos curiosos? As&iacute; es, durante 2 semanas estaremos publicando un dato curioso acerca de LUFantasie. &iexcl;Puedes ir a checarlo ahora mismo!</p>\r\n', 'LUFANTASIE'),
(159, '¡Oh! ¿Ya viste quién aparece?', '17 Marzo, 2020', '../imagenes/event-img/9c2f30ffb0.png', '<p style=\"text-align: justify;\">En <strong>LUFantasie </strong>han pasado muchos usuarios dispuestos a aportar su granito de arena para la web. Es por ello que por el Aniversario #6 decidimos recordarlos mediante algunas im&aacute;genes en nuestro <strong>Facebook</strong>.</p>\r\n', 'LUFantasie'),
(160, '¡Te reto!', '17 Marzo, 2020', '../imagenes/event-img/4fd8800e9f.png', '<p>&iexcl;Y tambi&eacute;n tenemos eventos en el <strong>hotel</strong>! No te pierdas de los <strong>Retos LUFantasie</strong> y gana incre&iacute;bles premios.</p>\r\n', 'LUFantasie'),
(161, '¡LUF-Consejos!', '17 Marzo, 2020', '../imagenes/event-img/37bcde112c.png', '<p style=\"text-align: justify;\">En nuestra p&aacute;gina de <strong>Facebook </strong>te estaremos dando algunos consejos para que tengas una mejor hobba-experiencia. &iexcl;No te pierdas de ninguno!</p>\r\n', 'LUFantasie'),
(162, '¿Ya conoces nuestro lugar de trabajo?', '17 Marzo, 2020', '../imagenes/event-img/45fcc19b53.png', '<p style=\"text-align: justify;\">&iexcl;Conoce nuestras <strong>Oficinas LUFantasie</strong>! Nuestro espacio laboral es tan importante para que nuestro trabajo tenga resultados de 5 estrellas. &iquest;A&uacute;n no le has echado un vistazo? &iexcl;Corre a nuestra p&aacute;gina de Facebook!</p>\r\n', 'LUFantasie'),
(163, '¡SORTEO!', '17 Marzo, 2020', '../imagenes/event-img/045f678a2c.png', '<p style=\"text-align: justify;\">&iquest;A&uacute;n no te enteras? &iexcl;Tenemos un sorteo para ti! Y tiene 3 premios disponibles para 3 diferentes usuarios. Dir&iacute;gete hacia nuestra p&aacute;gina de Facebook para enterarte de los requisitos para participar.</p>\r\n', 'LUFantasie'),
(164, '¿Fanático de Los Simpsons?', '5 Abril, 2020', '../imagenes/event-img/6381fac278.png', '<p style=\"text-align: justify;\">Si eres un gran fan&aacute;tico de <strong>Los Simpsons</strong>, este concurso es para ti. Nuestra Hotel Manager <strong>Luci </strong>nos trae un concurso en el que deber&aacute;s recrear alg&uacute;n sector caracter&iacute;stico de esta serie animada. &iquest;Quieres saber m&aacute;s? Da <a href=\"https://www.hobba.tv/news/concurso-de-construcci%C3%B3n\"><u><strong>click aqu&iacute;</strong></u></a> y conoce todos los requisitos y, por su puesto, el gran premio.</p>\r\n', 'LUFantasie'),
(165, 'A cocinar los ingredientes de crafteo', '5 Abril, 2020', '../imagenes/event-img/062de8f89b.png', '<p style=\"text-align: justify;\">En el mes de abril aterrizaron nuevas recetas de crafteo. Conoce toda la informaci&oacute;n dando <a href=\"https://www.facebook.com/photo?fbid=1537188209783703&amp;set=a.280304765472060\"><u><strong>click aqu&iacute;</strong></u></a>, &iexcl;y a conseguir todos los ingredientes!</p>\r\n', 'LUFantasie'),
(166, 'Información a la puerta de tu hobba-casa', '5 Abril, 2020', '../imagenes/event-img/ff6f5bbaa9.png', '<p style=\"text-align: justify;\">Prep&aacute;rate este 25 de abril para una charla en el Infobus. &iquest;Te formar&iacute;as en la fila desde ya? &iquest;O prefieres esperarte un opco m&aacute;s?</p>\r\n', 'LUFantasie'),
(167, '¡Participa con nosotros!', '5 Abril, 2020', '../imagenes/event-img/c69519aa99.png', '<p style=\"text-align: justify;\">Un sorteo m&aacute;s en <strong>LUFantasie</strong>, as&iacute; que atento a nuestro <strong>Facebook </strong>porque en cualquier momento ser&aacute; anunciado. &iexcl;No dudes en participar!</p>\r\n', 'LUFantasie'),
(168, 'Gymkhana de abril', '5 Abril, 2020', '../imagenes/event-img/b414f87b35.png', '<p style=\"text-align: justify;\">En LUFantasie tenemos la tabla de puntos actualizados. &iquest;No lo sabias? &iexcl;Da <a href=\"http://lufantasie.info/top-gym-puntos\"><u><strong>click aqu&iacute;</strong></u></a> y conoce el Top! No olvides asistir a la <strong>Gymkhana </strong>para seguir acumulando puntos.</p>\r\n', 'LUFantasie'),
(169, 'Capturadores de huevos, ¿eres uno de ellos?', '5 Abril, 2020', '../imagenes/event-img/77f100b285.png', '<p style=\"text-align: justify;\">Pr&oacute;ximamente tendremos un evento m&aacute;s disponible en el hotel. &iexcl;Atento para conseguir todo tipo de premios!</p>\r\n', 'LUFantasie'),
(170, '¡Ya llegamos a la final!', '3 Mayo, 2020', '../imagenes/event-img/a68216cd8d.png', '<p style=\"text-align: justify;\">El <strong>3 de mayo</strong> dar&aacute; lugar el final del torneo <strong>100 Hobbas Dijeron</strong>. &iexcl;No te lo pierdas! Con&eacute;ctate a <strong>Hobba </strong>antes de las <strong>22:00 HPE</strong> para que est&eacute;s puntual. &iquest;Quien ser&aacute; el ganador?</p>\r\n', 'LUFantasie'),
(171, 'Una edición más de este evento...', '1 Mayo, 2020', '../imagenes/event-img/fe10a80a2d.png', '<p style=\"text-align: justify;\">Otra oportunidad de sumar puntos. &iexcl;Participa! Solo deber&aacute;s construir una sala o un juego y llenarla con la mayor cantidad de usuarios posibles cada d&iacute;a.&nbsp;</p>\r\n', 'LUFantasie'),
(172, 'Hay que hacerse notar, ¿no?', '1 Mayo, 2020', '../imagenes/event-img/5c021ed92d.png', '<p style=\"text-align: justify;\">El gran y exclusivo <strong>VIP </strong>lleg&oacute; a Hobba y con ello muchas novedades pero solo para ciertos usuarios. &iquest;Quieres formar de este grupo de usuarios VIP?&nbsp; Da <a href=\"https://www.hobba.tv/vip/lounge\"><u><strong>click aqu&iacute;</strong></u></a> para m&aacute;s informaci&oacute;n.</p>\r\n', 'LUFantasie'),
(173, 'Payasos, magos, juegos... ¡Y más!', '1 Mayo, 2020', '../imagenes/event-img/829682f8a3.png', '<p style=\"text-align: justify;\">Como cada mes, se renov&oacute; el tema mensual y esta vez la <strong>feria </strong>invade nuestras salas en el hotel. Con ella nos traen muchos eventos en los que podr&aacute;s ganar una gran variedad de premios. Atento a <strong>LUFantasie </strong>para no perderte de ninguno. Y lo mejor de todo... &iexcl;Puedes prepararte para ellos desde ya! Da <a href=\"http://lufantasie.info/calendario\"><u><strong>click aqu&iacute;</strong></u></a> y conoce el <strong>calendario de eventos</strong> de este mes.</p>\r\n', 'LUFantasie'),
(174, '¡A sumar monedas!', '1 Mayo, 2020', '../imagenes/event-img/be04f61222.png', '<p style=\"text-align: justify;\">La moneda de este mes es nada menos y nada m&aacute;s que la <strong>ficha de oro</strong>. Con esta moneda podr&aacute;s comprar lo m&aacute;s exclusivo del hotel en la <strong>Tienda de Fichas de Oro</strong>. &iexcl;No te pierdas de ning&uacute;n evento!</p>\r\n', 'LUFantasie'),
(175, '¡A ganar se ha dicho!', '1 Mayo, 2020', '../imagenes/event-img/d72c5eaf3f.png', '<p style=\"text-align: justify;\">Este mes tramos nuevamente los puntos web. &iquest;C&oacute;mo puedes ganarlos? Participa en eventos de nuestro <a href=\"https://www.facebook.com/LUFantasieWeb\"><u><strong>Facebook </strong></u></a>y en nuestros juegos dentro del hotel.</p>\r\n', 'LUFantasie'),
(176, '¡A jugar!', '9 Mayo, 2020', '../imagenes/event-img/54572622d1.png', '<p style=\"text-align: justify;\">La Feria en Hobba sigue en pie. &iexcl;Es hora de jugar! Asiste a los juegos diarios y podr&aacute;s ganar fichas de oro para pr&oacute;ximas compras exclusivas.</p>\r\n', 'LUFantasie'),
(177, '¡Aaaah! Cuidado con los sustos...', '9 Mayo, 2020', '../imagenes/event-img/2d34ec1709.png', '<p style=\"text-align: justify;\">Gana un incre&iacute;ble premio en la casa embrujada de Valunika. &iexcl;Menos mal que nadie vive ah&iacute;! Los espantos que te puedes llevar...</p>\r\n', 'LUFantasie'),
(178, '¡No te caigas!', '9 Mayo, 2020', '../imagenes/event-img/8d99ae20f2.png', '<p style=\"text-align: justify;\">Tampoco te pierdas de la cuerda loca. &iexcl;Y muy loca! Porque en cualquier momento... &iexcl;te har&aacute; caer!&nbsp;</p>\r\n', 'LUFantasie'),
(179, 'Sin resbalones, ¿o sí?', '9 Mayo, 2020', '../imagenes/event-img/09d5b8ac92.png', '<p style=\"text-align: justify;\">Parece que seguimos cay&eacute;ndonos... &iexcl;En Rollers Race! Cuidado con los resbalones o perder&aacute;s. &iquest;Listo? &iexcl;Pues a ello!&nbsp;</p>\r\n', 'LUFantasie'),
(180, '¡No olvides tu pase!', '9 Mayo, 2020', '../imagenes/event-img/9448b39a9e.png', '<p style=\"text-align: justify;\">Para poder asistir a los eventos de la feria necesitar&aacute;s portar tu pase de feria. &iquest;Largas filas? &iexcl;Horas desperdiciadas! As&iacute; que puedes conseguir un pase VIP para saltarte todas esas colas sin fin.</p>\r\n', 'LUFantasie'),
(181, '¿Quieres ser exclusivo?', '9 Mayo, 2020', '../imagenes/event-img/10d2825716.png', '<p style=\"text-align: justify;\">Tenemos disponible en nuestro Instagram (@lufantasie) un sorteo en el que podr&aacute;s ganar 2 VIP. &iquest;A&uacute;n no has participado? &iexcl;Ve a ello!</p>\r\n', 'LUFantasie'),
(182, '¡A acumular horas!', '22 Mayo, 2020', '../imagenes/event-img/5e20cdb531.png', '<p style=\"text-align: justify;\">&iquest;Recuerdas las zapatillas Vans? &iexcl;Est&aacute;n de regreso! Tan solo deber&aacute;s estar conectado al hotel durante 150 horas... &iexcl;y listo! Los primeros 200 en conseguir esta cantidad de horas acumuladas obtendr&aacute;n sus vans.</p>\r\n', 'LUFantasie'),
(183, '¡Somos Oficiales!', '22 Mayo, 2020', '../imagenes/event-img/64a8f89327.png', '<p style=\"text-align: justify;\">As&iacute; es, LUFantasie por segunda vez se ha convertido en una de las WebFans Oficiales de Hobba junto a Hobbugs. &iexcl;Enhorabuena! Muchas gracias a todos por su apoyo hacia la web. &iexcl;Siempre al tanto de ti!</p>\r\n', 'LUFantasie'),
(184, '¡Nueva edición!', '24 Mayo, 2020', '../imagenes/event-img/ddb3011883.png', '<p style=\"text-align: justify;\">Las ediciones de <strong>GOTW &amp; ROTW</strong> siguen en pie y esta vez la tem&aacute;tica es de <strong>Festival Bohemio</strong>. &iquest;Est&aacute;s preparado? &iexcl;A preparar tus salas! Y env&iacute;a tus pantallazos a <strong>concursoshobba@gmail.com</strong>.</p>\r\n', 'LUFantasie'),
(185, '¡Seguimos con las atracciones!', '22 Mayo, 2020', '../imagenes/event-img/96115249f6.png', '<p style=\"text-align: justify;\">Los juegos de la feria siguen en pie junto a los equipos que los abren para tu diversi&oacute;n diaria. &iquest;A&uacute;n no has participado? &iexcl;A&uacute;n tienes oportunidad! Y gana incre&iacute;bles premios como las <strong>fichas de oro </strong>para poder comprar en la <strong>Tienda de Fichas de Oro</strong>.</p>\r\n', 'LUFantasie'),
(186, 'Este viernes... ¡Unas sorpresas!', '22 Mayo, 2020', '../imagenes/event-img/ecbec20a09.png', '<p style=\"text-align: justify;\">Asiste a la subasta especial de este viernes para obtener tus LTDs favoritos o... &iexcl;La mejor ropa del hotel! No te lo pierdas y prepara tus THs para ofrecer en las subastas.</p>\r\n', 'LUFantasie'),
(187, '¡A por el ultra!', '22 Mayo, 2020', '../imagenes/event-img/a0b3e02e65.png', '<p>Por parte del <strong>Game Manager randy212</strong> se viene el <strong>laberinto </strong>&quot;La Feria&quot;. &iquest;Y cu&aacute;l es el premio? Nada m&aacute;s ni nada menos que un <strong>ultra</strong>. &iexcl;As&iacute; es! &iquest;Te vas a perder esta oportunidad?</p>\r\n', 'LUFantasie'),
(188, 'Rojo, azul, verde...', '1 Junio, 2020', '../imagenes/event-img/bc4de25986.png', '<p style=\"text-align: justify;\">&iexcl;Nuevo mes, nuevo tema! S&iacute;, porque cada mes se renueva el tema en todo el hotel. &iquest;Qu&eacute; significa esto? Significa que los eventos tendr&aacute;n esa tem&aacute;tica. &iquest;Hab&iacute;as o&iacute;do decir que junio es el mes de la diversidad? &iexcl;Pues as&iacute; es! Porque en todo el mes estaremos celebrando el <strong>D&iacute;a del Orgullo LGBT</strong>. Es por ello que el mes de junio en <strong>Hobba </strong>tratar&aacute; sobre <strong>los colores</strong>.</p>\r\n', 'LUFantasie'),
(189, '¡A sumar puntos!', '1 Junio, 2020', '../imagenes/event-img/ca04c2f26e.png', '<p style=\"text-align: justify;\">La <strong>Gymkhana </strong>ha vuelto y con ella un gran premio. Recordemos que la Gymkhana trata sobre acumular puntos de lunes a jueves mediante un juego distinto al que deber&aacute;s calificar con un juego de banzais. El ganador al final del mes tendr&aacute; un gran premio.</p>\r\n', 'LUFantasie'),
(190, '¿Ya conseguiste todos los ingredientes?', '1 Junio, 2020', '../imagenes/event-img/2387fc825c.png', '<p style=\"text-align: justify;\">Como casi cada mes, tenemos una nueva receta para craftear y conseguir incre&iacute;bles premios. Los ingredientes los deber&aacute;s conseguir mediante los eventos que se realicen en el hotel. &iquest;Y cu&aacute;l es la receta? &iexcl;Pronto lo sabremos! Atento a nuestras noticias.</p>\r\n', 'LUFantasie'),
(191, '¿Qué evento tocará hoy?', '1 Junio, 2020', '../imagenes/event-img/f99062d55f.png', '<p style=\"text-align: justify;\">En <strong>LUFantasie </strong>ya contamos con el <strong>calendario de eventos</strong> con el cual podr&aacute;s ver los eventos que se realizar&aacute;n en el hotel en cada d&iacute;a del mes.&nbsp;</p>\r\n', 'LUFantasie'),
(192, '¿Nuevos artículos?', '1 Junio, 2020', '../imagenes/event-img/3bc57ba404.png', '<p style=\"text-align: justify;\">Ya lleg&oacute; la hora de renovar el VIP, &iquest;no crees? &iexcl;Espero que lo hagas! Pues ya podr&aacute;s conseguir diferentes art&iacute;culos al renovarlo. &iexcl;Descubre cu&aacute;les son!</p>\r\n', 'LUFantasie'),
(193, 'Diamantes... ¡Muchos diamantes!', '1 Junio, 2020', '../imagenes/event-img/aa1763b548.png', '<p style=\"text-align: justify;\">&iquest;Sab&iacute;as que...? &iexcl;En los eventos de <strong>LUFantasie </strong>ya puedes ganar m&aacute;s premios! Diamantes, placa, patos... &iexcl;y m&aacute;s! Asiste a nuestros juegos diarios y participa en los eventos de <strong>Facebook </strong>e <strong>Instagram </strong>(<strong>@lufantasie</strong>)&nbsp;para ganar.</p>\r\n', 'LUFantasie'),
(194, '¡A craftear ingredientes!', '6 Junio, 2020', '../imagenes/event-img/33b57b8f52.png', '<p style=\"text-align: justify;\">&iexcl;A juntar bombas de colores! Cada equipo del hotel estar&aacute; entregando diferentes colores de bombas con las que podr&aacute;s craftear <strong>diferentes ojos</strong> para crear una <strong>pi&ntilde;ata </strong>de la que saldr&aacute;n varios premios. &iquest;Ya sabes cu&aacute;les son? &iexcl;Apres&uacute;rate a acumular bombas!</p>\r\n', 'LUFantasie'),
(195, '¿Te gusta crear salas? ¡Esto es para ti!', '6 Junio, 2020', '../imagenes/event-img/ea950ccd51.png', '<p style=\"text-align: justify;\"><strong>Nuevos furnis</strong> se han a&ntilde;adido al cat&aacute;logo, as&iacute; que esta es tu oportunidad para crear incre&iacute;bles salas. &iquest;Pero cu&aacute;l es la secci&oacute;n de estos furnis? &iexcl;<strong>India</strong>! No te puedes perder de esta nueva secci&oacute;n con la que puedes ambientar tus salas. &iquest;Qu&eacute; esperas?</p>\r\n', 'LUFantasie'),
(196, '¡Nuevo evento de construcción!', '6 Junio, 2020', '../imagenes/event-img/85f905f86c.png', '<p style=\"text-align: justify;\">La <strong>Hotel Manager Luci</strong> nos ha preparado un evento de construcci&oacute;n para todo el hotel. &iquest;De qu&eacute; tratar&aacute;? &iexcl;Da <a href=\"http://lufantasie.info/noticias?article=467\"><strong>click aqu&iacute;</strong></a>! Y conoce todos los detalles para que puedas comenzar a participar.&nbsp;</p>\r\n', 'LUFantasie'),
(197, '¿Nuevo laberinto?', '6 Junio, 2020', '../imagenes/event-img/2649682a2c.png', '<p style=\"text-align: justify;\">En el hotel tenemos disponible un <strong>laberinto </strong>que va acorde al tema de este mes: <strong>colores/LGBT</strong>. Fue creado por MOD-Cysero y nos da la oportunidad de pasar un buen rato de diversi&oacute;n durante todo el mes. &iexcl;Puedes ganar diferentes <strong>premios</strong>! Asiste al laberinto y descubre cu&aacute;les son.</p>\r\n', 'LUFantasie'),
(198, '¡A acumular horas!', '6 Junio, 2020', '../imagenes/event-img/c2f95dec58.png', '<p style=\"text-align: justify;\">Las <strong>orejas de rata</strong> siguen disponibles en el hotel con tan solo acumular horas conectado. &iexcl;Debes conseguir <strong>150 horas</strong>! No te pierdas esta oportunidad tan f&aacute;cil de conseguir esta prenda.</p>\r\n', 'LUFantasie'),
(199, '¡Gana con LUFantasie!', '6 Junio, 2020', '../imagenes/event-img/f1ef3be2bd.png', '<p style=\"text-align: justify;\">En nuestros distintos eventos estaremos entregando <strong>bombas rosas de crafteo</strong> a los ganadores. Participa en los <strong>eventos </strong>de nuestro Facebook y en nuestros <strong>juegos </strong>del hotel. &iexcl;No te lo pierdas!</p>\r\n', 'LUFantasie'),
(200, '¡El verano ha llegado!', '30 Junio, 2020', '../imagenes/event-img/1560bef146.png', '<p style=\"text-align: justify;\">As&iacute; es, como en muchas partes del mundo en Hobba el verano tambi&eacute;n aterriz&oacute; con sus altas temperaturas. Asi que no te puedes perder de ning&uacute;n evento que se viene con este mes tan caluroso en el hotel. &iquest;Cu&aacute;les ser&aacute;s las pr&oacute;ximas novedades? &iexcl;Qu&eacute;date en LUFantasie para enterarte de ellas al minuto!</p>\r\n', 'LUFantasie'),
(201, '¿Conseguiste estar on 200 horas?', '30 Junio, 2020', '../imagenes/event-img/33e1bc5b58.png', '<p style=\"text-align: justify;\">No es novedad que la mayor&iacute;a de nosotros estamos encantados con la nueva <strong>ropa LTD</strong>. Recientemente se a&ntilde;adi&oacute; una nueva prenda que solo podr&aacute;s obtener si te conectas al hotel por <strong>200 horas</strong>. &iexcl;No pierdas esta oportunidad! El <strong>chaleco salvavidas</strong> est&aacute; esperando a que sea usado entre los usuarios.</p>\r\n', 'LUFantasie'),
(202, '¡Se parte de nosotros!', '30 Junio, 2020', '../imagenes/event-img/4a33116b42.png', '<p style=\"text-align: justify;\">En LUFantasie es importante contar con un buen equipo para brindar contenido de calidad. &iquest;Te interesa formar parte de nosotros? &iexcl;Estamos en busca de <strong>Reporteros </strong>y <strong>Redes Sociales</strong>!&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\"><strong>Reporteros</strong>: se encargan de la redacci&oacute;n de las <strong>noticias</strong>. Contacta con <strong>MOD-Bambx</strong> (Bambi Lu) o <strong>Stevan </strong>(Stevan Rodriguez) si est&aacute;s interesado.</p>\r\n\r\n<p style=\"text-align: justify;\"><strong>Redes </strong>Sociales: se encargan de la realizaci&oacute;n de <strong>eventos mediante Facebook</strong>. Contacta con <strong>issua </strong>(Issua Lu) si est&aacute;s interesado.</p>\r\n', 'LUFantasie'),
(203, '¡Pero cuántos eventos con gran premio!', '30 Junio, 2020', '../imagenes/event-img/e8af324218.png', '<p style=\"text-align: justify;\">Julio viene con grandes eventos adem&aacute;s de los habituales. &iexcl;Hay que festejar bien el verano! En este mes podr&aacute;s contar con ciertos eventos con un gran premio al final: <strong>San Fermin</strong>, <strong>KickWars</strong>, <strong>Carreras de relevos</strong> y <strong>Formula 1</strong>.</p>\r\n', 'LUFantasie'),
(204, '¡Nueva CA Oficial!', '30 Junio, 2020', '../imagenes/event-img/1b7d460688.png', '<p style=\"text-align: justify;\">Como en cada estaci&oacute;n, la <strong>Central de Ayuda Oficial</strong> toma la tem&aacute;tica correspondiente y esta vez no fue la excepci&oacute;n. El usuario <strong>LeoFabio </strong>fue quien le dio una nueva vista a nuestras dudas con su central ganadora. &iexcl;&Eacute;chale un vistazo!</p>\r\n', 'LUFantasie'),
(205, '¿Más sorpresas?', '30 Junio, 2020', '../imagenes/event-img/70f7e76d05.png', '<p style=\"text-align: justify;\">En LUFantasie procuramos preparar nuevos eventos para tu diversi&oacute;n. &iexcl;Este mes seguiremos con las sorpresas! Atento al hotel para no perderte de un nuevo evento con un premio especial.</p>\r\n', 'LUFantasie'),
(206, 'GOTW y ROTW', '13 Julio, 2020', '../imagenes/event-img/b6eaeac7eb.png', '<p style=\"text-align: justify;\">Una nueva edici&oacute;n de esta grandiosa serie de eventos ha aterrizado. &iexcl;No te pierdas de esta nueva oportunidad para ganar! Lee toda la informaci&oacute;n en nuestras noticias.</p>\r\n', 'LUFantasie'),
(207, 'GOTW y ROTW', '13 Julio, 2020', '../imagenes/event-img/919c8d3232.png', '<p style=\"text-align: justify;\">Una edici&oacute;n m&aacute;s de esta grandiosa serie de eventos ha aterrizado en el hotel. &iexcl;Otra oportunidad m&aacute;s para ganar! As&iacute; que no te lo puedes perder. Lee toda la informaci&oacute;n en nuestras noticias.</p>\r\n', 'LUFantasie'),
(208, 'Un terrorífico evento regresa...', '13 Julio, 2020', '../imagenes/event-img/4b0b676af5.png', '<p style=\"text-align: justify;\">&iexcl;No te lo pierdas! Este 18 de julio el SAW vuelve a abrir sus puertas en su gran castillo. &iquest;Cu&aacute;les seran las novedades?</p>\r\n', 'LUFantasie'),
(209, '¡Acumula 150 horas!', '13 Julio, 2020', '../imagenes/event-img/ea9c3a23cb.png', '<p style=\"text-align: justify;\">La cola de rata llega al hotel por durar 150 horas on en el hotel. &iquest;Podr&aacute;s conseguirla? &iexcl;No te pierdas de esta oportunidad de ganar algo gratis!</p>\r\n', 'LUFantasie'),
(210, '¿Serás el maestro en tradeos?', '13 Julio, 2020', '../imagenes/event-img/9be81e9a6c.png', '<p style=\"text-align: justify;\">Un nuevo evento abraza a todos los tradeadores expertos en Hobba para poner a prueba sus conocimientos en el hobba-mundo del tradeo. &iquest;Eres uno de ellos? &iexcl;Atento a las fechas! No te puedes perder de este gran evento.</p>\r\n', 'LUFantasie'),
(211, '¡Asiste a los conciertos!', '13 Julio, 2020', '../imagenes/event-img/486be25b2c.png', '<p style=\"text-align: justify;\">VISA Icons se encarga de traernos conciertos de grandes cantantes como Shakira, Jennifer Lopez, Queen, etc. En LUFantasie te recomendamos al 100% que no te pierdas de ning&uacute;n concierto, pues cada uno es una incre&iacute;ble experiencia de la que no te puedes perder.&nbsp;</p>\r\n', 'LUFantasie');
INSERT INTO `eventos` (`id`, `titulo`, `dia_del_evento`, `imagen`, `descripcion`, `publicador`) VALUES
(212, '¿Solecitos? ¡Cuack!', '13 Julio, 2020', '../imagenes/event-img/b7d2f829c3.png', '<p style=\"text-align: justify;\">En LUFantasie venimos a entretenerte adem&aacute;s de brindarte informaci&oacute;n, as&iacute; que como cualquier evento te tenemos premios que puedes ganar en nuestros juegos y en eventos de Facebook.</p>\r\n', 'LUFantasie'),
(213, 'A vestirse a la moda...', '23 Julio, 2020', '../imagenes/event-img/9ea967971f.png', '<p style=\"text-align: justify;\">Los <strong>Bonus Rare</strong> no dejan descansar a nuestras computadoras. &iexcl;No s&eacute; si estar feliz o enojado! Pero bueno, esta vez si duras <strong>150 horas</strong> on en el hotel, podr&aacute;s ganar una prenda nueva y &uacute;nica. &iexcl;Un <strong>accesorio de scout</strong>!&nbsp;</p>\r\n', 'LUFantasie'),
(214, '¡A rodar! Espera, ¿qué?', '23 Julio, 2020', '../imagenes/event-img/172e2466da.png', '<p style=\"text-align: justify;\">As&iacute; es, ahora es momento de &iexcl;RODAR! &iquest;Por qu&eacute;? Porque este concurso se trata de hacer una <strong>casa rodante</strong>. &iexcl;S&iacute;! As&iacute; como lo lees. &iquest;Est&aacute;s participando? &iexcl;Mucha suerte y gana el gran premio!</p>\r\n', 'LUFantasie'),
(215, '¡Buenas noticias!', '23 Julio, 2020', '../imagenes/event-img/e49f399656.png', '<p style=\"text-align: justify;\">Si no has le&iacute;do nuestras &uacute;ltimas noticias, este es tu momento para hacerlo. &iexcl;Ent&eacute;rate de los <strong>nuevos ascensos</strong>! Y no olvides felicitar a los ascendidos por su gran esfuerzo de llegar a su puesto.</p>\r\n', 'LUFantasie'),
(216, '¿Tienes un pato... de mascota?', '23 Julio, 2020', '../imagenes/event-img/184e39fa95.png', '<p style=\"text-align: justify;\">Si tienes cualquier otra mascota tambi&eacute;n es v&aacute;lido, pues la charla de infobus que se aproxima se trata de estos tan queridos animalitos. &iexcl;No te lo puedes perder!</p>\r\n', 'LUFantasie'),
(217, '¡El pulpo necesita tu ayuda!', '23 Julio, 2020', '../imagenes/event-img/2c2f7ac154.png', '<p style=\"text-align: justify;\">En este evento de la <strong>Hotel Manager Lila</strong> tendr&aacute;s que salvar al pulpo de su muerte. &iexcl;No faltes! O el pulpo no tendr&aacute; un final feliz... Dale un vistazo al calendario de eventos para m&aacute;s informaci&oacute;n.</p>\r\n', 'LUFantasie'),
(218, '¡A dibujar un pato! ¿Un pato?', '23 Julio, 2020', '../imagenes/event-img/119668501e.png', '<p style=\"text-align: justify;\">En el evento de la <strong>Hotel Manager Luci</strong> los elegidos tendr&aacute;n que dibujar lo que la <strong>HM </strong>indique. &iquest;Eres todo un artista? &iexcl;Este evento es para ti! Demuestra tus dotes de dibujante para ganar.</p>\r\n', 'LUFantasie'),
(219, '¡Arriba los vikingos!', '1 Agosto, 2020', '../imagenes/event-img/328543894f.png', '<p style=\"text-align: justify;\">La Edad Media aterriza en todo el hotel, por lo que en LUFantasie tambien nos vestimos como medievales.&nbsp; &iexcl;Ent&eacute;rate del crafteo y de pr&oacute;ximos eventos! Solo en LUFantasie.</p>\r\n', 'LUFantasie'),
(220, '¡A craftear!', '1 Agosto, 2020', '../imagenes/event-img/211ee31307.png', '<p style=\"text-align: justify;\">Deber&aacute;s recolectar lingotes de oro para poder crear una armadura completa y ensamblarla. &iexcl;Qui&eacute;n sabe lo que contendr&aacute; por dentro! &iquest;Una cacota? &iexcl;O un lila! Es un misterio...</p>\r\n', 'LUFantasie'),
(221, 'Este banzai... o este...', '1 Agosto, 2020', '../imagenes/event-img/03833dda4d.png', '<p style=\"text-align: justify;\">Hora de acumular puntos con este gran evento que se repite cada dos meses... &iexcl;La Gymkhana ha llegado! Supera la prueba de los banzai para poder quedar entre los jugadores que pasar&aacute;n a un juego eliminatorio y sumar 1 punto si eres el ganador.</p>\r\n', 'LUFantasie'),
(222, 'En busca del tesoro...', '1 Agosto, 2020', '../imagenes/event-img/e4cd06b40e.png', '<p style=\"text-align: justify;\">LUFantasie te trae nuevamente una oportunidad para ganar un Ultra... &iexcl;El Panda Malva! As&iacute; es, pero esta vez tendr&aacute;s que ir en busca del tesoro.&nbsp;</p>\r\n', 'LUFantasie'),
(223, '¡Te reto!', '1 Agosto, 2020', '../imagenes/event-img/ac2b552d10.png', '<p style=\"text-align: justify;\">Los retos en Hobba hab&iacute;an estado inactivos por mucho tiempo hasta el d&iacute;a de hoy. &iexcl;Al fin se han renovado! Y esta vez llega el Battle Pass al hotel. &iquest;Qu&eacute; significa esto? Significa que... &iexcl;Podr&aacute;s subir de nivel! As&iacute; es, con cada reto que acompletes subir&aacute;s de experiencia y asimismo de nivel.</p>\r\n', 'LUFantasie'),
(224, '¡No te pierdas! Sigue el cuack...', '1 Agosto, 2020', '../imagenes/event-img/90a1e2c0fe.png', '<p style=\"text-align: justify;\">En pr&oacute;ximos eventos podr&aacute;s encontrar uno que otro laberinto que deber&aacute;s pasar hasta el final para ganar el gran premio. &iquest;Te lo vas a perder?</p>\r\n', 'LUFantasie'),
(227, '¡Todos los eventos disponibles!', '8 Septiembre, 2020', '../imagenes/event-img/15076b89b2.png', '<p>Este mes de aniversario Hobba Hotel no te despegues de todos los eventos que trae el equipo administrativo, puedes encontrar cada evento de forma detallada en nuestro calendario. &iquest;Te lo vas a perder? <u><strong><a href=\"http://lufantasie.info/calendario\"><span style=\"color:#0000CD\">Clic aqu&iacute;</span></a></strong></u></p>\r\n', 'LUFantasie'),
(228, '¡Nueva red social!', '8 Septiembre, 2020', '../imagenes/event-img/339a5b3ddd.png', '<p>Nuestra Comunidad Hobba sigue creciendo de forma diaria y con ello sus redes sociales, &iexcl;As&iacute; es! De ahora en m&aacute;s se suma una nueva red social. &iquest;<strong>TikTok</strong>? Para seguirlos debes darle <u><strong><a href=\"https://www.tiktok.com/@comunidadhobba\"><span style=\"color:#0000FF\">clic aqu&iacute;</span></a></strong></u></p>\r\n', 'LUFantasie'),
(229, '¡Trono aniversario XIII!', '8 Septiembre, 2020', '../imagenes/event-img/ff5e808bf0.png', '<p>&iexcl;Crafteo por doquier! No esperes m&aacute;s y compra tus Tronos de Aniversario Hobba XIII a un precio accesible en el cat&aacute;logo. &iexcl;A por ello! Para saber todas las recompensas te recomendamos que leas nuestra noticia dando <u><strong><a href=\"http://lufantasie.info/noticias?article=719\"><span style=\"color:#0000FF\">clic aqu&iacute;</span></a></strong></u></p>\r\n', 'LUFantasie'),
(230, 'El premio es para...', '8 Septiembre, 2020', '../imagenes/event-img/6220cb7535.png', '<p>&iexcl;No esperes m&aacute;s y compra tu n&uacute;mero de loter&iacute;a! Hobba acaba de lanzar el mu&ntilde;eco donde podr&aacute;s ganar fant&aacute;sticos premios si sale tu n&uacute;mero. Lo puedes encontrar en el cat&aacute;logo a un precio accesible. &iexcl;Ediciones limitadas!</p>\r\n', 'LUFantasie'),
(231, 'Seguimos innovando ', '8 Septiembre, 2020', '../imagenes/event-img/fbffb10f64.png', '<p>&iexcl;Recuerda que puedes seguirnos en nuestras redes sociales! LUFantasie cuenta con Instagram donde subimos contendio de forma diaria para que puedas participar, divertirte e informarte de todo lo que sucede. &iquest;Nos sigues? <u><strong><a href=\"https://www.instagram.com/lufantasie/\"><span style=\"color:#0000FF\">Clic aqu&iacute;</span></a></strong></u></p>\r\n', 'LUFantasie'),
(232, '¡13 años Hobba!', '8 Septiembre, 2020', '../imagenes/event-img/55c98293e4.png', '<p>&iexcl;Festejemos junto con Hobba Hotel! Nuestra querida comunidad est&aacute; de aniversario, y cumple nada m&aacute;s ni nada menos que... &iexcl;13 a&ntilde;os! No te despegues de ning&uacute;n evento que trae el equipo de Hobba y LUFantasie. &iexcl;Diversi&oacute;n de forma diaria!</p>\r\n', 'LUFantasie');
