INSERT INTO `hobba_rangos` (`id`, `nombre`, `descripcion`, `encargados`, `placa`, `orden`, `clas`) VALUES
(1, 'Administradores', 'Son aquellos que se encargan de actualizar, arreglar errores para que el Hotel siempre este activo.', '<img src=\"https://i.imgur.com/PdVIFiq.png\" alt=\"Salinass\" class=\"manager-img\">El encargado de este rango es <b>Salinass</b>.', 'imagenes/hobba-equipo-placas/staff.png', 1, 'Primario'),
(2, 'CO-Administradores', 'Son aquellos que ayudan a los administradores pues son una mano derecha.', '<img src=\"https://i.imgur.com/PdVIFiq.png\" alt=\"Salinass\" class=\"manager-img\">El encargado de este rango es <b>Salinass</b>.', 'imagenes/hobba-equipo-placas/staff.png', 2, 'Primario'),
(3, 'Hotel Managers', 'Son aquellos que se encargan de entretener a todo el Hotel, crean eventos, juegos, concursos, torneos con el fin de dar diversi&oacute;n.', '<img src=\"https://i.imgur.com/PdVIFiq.png\" alt=\"Salinass\" class=\"manager-img\">El encargado de este rango es <b>Salinass</b>.', 'imagenes/hobba-equipo-placas/staff.png', 3, 'Primario'),
(4, 'Moderadores', 'Son aquellos que se encargan de sancionar a aquel que no cumpla con la manera hobba.', '<img src=\"https://i.imgur.com/IWWQS4P.png\" alt=\"Hugor\" class=\"manager-img\">El encargado de este rango es <b>Hugor</b>.', 'imagenes/hobba-equipo-placas/mod.png', 4, 'Primario'),
(5, 'Community Managers', 'Son aquellos que se encargan de atender dudas en la Central oficial y ayudan a resolver reportes.', '<img src=\"https://i.imgur.com/PdVIFiq.png\" alt=\"Salinass\" class=\"manager-img\"> <img src=\"https://i.imgur.com/8OdAffv.png\" class=\"manager-img\" alt=\"Luci\"> Los encargados de este rango son <b>Salinass</b> y <b>Luci</b>.', 'https://i.imgur.com/Tbj4ZV3.gif', 5, 'Primario'),
(6, 'Hobba Guías', 'Son aquellos que se encargan de resolver tus dudas en la Central de Ayuda Oficial.', '<img src=\"https://i.imgur.com/PdVIFiq.png\" alt=\"Salinass\" class=\"manager-img\"> <img src=\"https://i.imgur.com/8OdAffv.png\" class=\"manager-img\" alt=\"Luci\"> Los encargados de este rango son <b>Salinass</b> y <b>Luci</b>.', 'https://i.imgur.com/LCGGmTU.gif', 6, 'Primario'),
(7, 'Croupier\'s', 'Son aquellos son de m&aacute;xima confianza, pues nunca te timar&aacute;n en el Casino.', '<img src=\"https://i.imgur.com/YjHyVIW.png\" alt=\"Hugor\" class=\"manager-img\"><img src=\"https://i.imgur.com/F7Lo9DF.png\" alt=\"Nighty\" class=\"manager-img\"> El encargado es <b>Hugor</b> y su ayudante <b>Nighty</b>.', 'imagenes/hobba-equipo-placas/croupier.png', 10, 'Secundario'),
(8, 'Game Manager', 'Son aquellos que nos entretienen dentro del Hotel, haciendo maravillosos juegos para entretenerte.', '<img src=\"https://i.imgur.com/SdDVNY3.png\" alt=\"Hugor\" class=\"manager-img\">El encargado de este rango es <b>Hugor</b>.', 'https://i.imgur.com/ZcCCXuS.png', 9, 'Primario'),
(9, 'DJ\'s', 'Son aquellos que le dan m&uacute;sica a tus o&iacute;dos en el Hotel, adem&aacute;s de eventos por la Radio de Hobba.', '<img src=\"https://i.imgur.com/3wAPM2f.png\" alt=\"AndresMaxima\" class=\"manager-img\">El encargado de este rango es <b>MOD-AndresMaxima</b>.', 'https://i.imgur.com/xycAkc9.gif', 11, 'Secundario'),
(10, 'Publicistas', 'Son aquellos que ayudan al Hotel a incrementar la cantidad de usuarios online publicitando en otro Hoteles.', '<img src=\"https://i.imgur.com/3gVoHem.png\" alt=\"Luci\" class=\"manager-img\"><img src=\"https://i.imgur.com/tb0QxJ9.png\" alt=\"Akanix\" class=\"manager-img\">La encargada es <b>Luci</b> y su ayudante <b>Akanix</b>.', 'https://i.imgur.com/3zCkDeL.gif', 13, 'Secundario'),
(11, 'Diseñador Experto', 'Son aquellos que nos brinda entretenimiento por redes sociales mediante las cuentas Oficiales de Hobba (Facebook/Twitter/Instagram/Youtube).', '<img src=\"https://i.imgur.com/G7i9bE8.png\" alt=\"Lila\" class=\"manager-img\">La encargada de este rango es <b>CM-Miau</b>.', 'https://i.imgur.com/dvGZXbS.png', 14, 'Secundario'),
(14, 'Room Builder\'s', 'Se encargan de hacer Salas Oficiales para Hobba.', '<img src=\"https://i.imgur.com/8OdAffv.png\" alt=\"Luci\" class=\"manager-img\">El encargado de este rango es <b>Luci</b>.', 'https://i.imgur.com/Hts4c8J.gif', 16, 'Secundario'),
(15, 'Jefes de Publicidad', 'Son aquellos que ayudan al Hotel a incrementar la cantidad de usuarios online publicitando en otro Hoteles.', '<img src=\"https://i.imgur.com/3gVoHem.png\" alt=\"Luci\" class=\"manager-img\"><img src=\"https://i.imgur.com/tb0QxJ9.png\" alt=\"Akanix\" class=\"manager-img\">La encargada es <b>Luci</b> y su ayudante <b>Akanix</b>.', 'https://i.imgur.com/TnUjImd.png', 12, 'Secundario'),
(16, 'Automáticos', 'Son aquellos que realizan juegos 24/7 para aumentar la diversión en el hotel.', '<img src=\"https://i.imgur.com/eO90JeW.png\" alt=\"Eduardd\" class=\"manager-img\">El encargado de este rango es <b>Eduardd</b>.', 'https://i.imgur.com/qYKI5Zt.gif', 17, 'Secundario'),
(17, 'Hobba Alfas', 'Son aquellos que atienden peticiones de ayuda de los usuarios por la herramienta :help o :ayuda', '<img src=\"https://i.imgur.com/84ykL2c.png\" alt=\"Mir\" class=\"manager-img\">El encargado de este rango es <b>Mir</b>.', 'https://i.imgur.com/qZqDyt4.png', 19, 'Secundario'),
(18, 'Master Trade', 'Son aquellos que regulan los precios de los rares realizando subastas y eventos relacionados con estos furnis.', '<img src=\"https://i.imgur.com/S7eu71U.png\" alt=\"Valunika\" class=\"manager-img\"><img src=\"https://i.imgur.com/CBJaMQy.png\" alt=\"Injustice\" class=\"manager-img\">La encargada es <b>Valunika</b> y su ayudante <b>Injustice</b>.', 'https://i.imgur.com/UrKW3WD.png', 18, 'Secundario'),
(19, 'Game Manager Experto', 'Son aquellos que nos entretienen dentro del Hotel, haciendo maravillosos juegos para entretenerte.', '<img src=\"https://i.imgur.com/SdDVNY3.png\" alt=\"Hugor\" class=\"manager-img\">El encargado de este rango es <b>Hugor</b>.', 'https://i.imgur.com/TLxgfeC.png', 8, 'Primario'),
(20, 'Master Game Manager', 'Son aquellos que nos entretienen dentro del Hotel, haciendo maravillosos juegos para entretenerte.', '<img src=\"https://i.imgur.com/SdDVNY3.png\" alt=\"Hugor\" class=\"manager-img\">El encargado de este rango es <b>Hugor</b>.', 'https://i.imgur.com/jo53U1D.png', 7, 'Primario'),
(21, 'Diseñador Digital', 'Son aquellos que nos brinda entretenimiento por redes sociales mediante las cuentas Oficiales secundarias de Hobba (Facebook).', '<img src=\"https://i.imgur.com/G7i9bE8.png\" alt=\"Lila\" class=\"manager-img\">La encargada de este rango es <b>CM-Miau</b>.', 'https://i.imgur.com/oXTIUqr.png', 15, 'Secundario');
